$.fn.dataTable.ext.order["dom-text"]=function(e,n){return this.api().column(n,{order:"index"}).nodes().map((function(e,n){return $("input",e).val()}))},$.fn.dataTable.ext.order["dom-text-numeric"]=function(e,n){return this.api().column(n,{order:"index"}).nodes().map((function(e,n){return 1*$("input",e).val().replace(/\D/g,"").replace(/\B(?=(\d{3})+(?!\d))/g,"")}))},$.fn.dataTable.ext.order["dom-select"]=function(e,n){return this.api().column(n,{order:"index"}).nodes().map((function(e,n){return $("select",e).val()}))},$.fn.dataTable.ext.order["dom-checkbox"]=function(e,n){return this.api().column(n,{order:"index"}).nodes().map((function(e,n){return $("input",e).prop("checked")?"1":"0"}))};